definition.script:
pipeline {
    agent {
		label "slave-1"
    }
    parameters {
        string(defaultValue: "", description: '', name: 'VERSION')
    }
    environment {
        NEXUS_VERSION = "nexus3"
        NEXUS_PROTOCOL = "https"
		NEXUS_URL = "{{ hostvars.nexus_linux.ansible_host_dns }}:8443"
        NEXUS_REPOSITORY = "petclinic-maven-hosted-release"
        NEXUS_URL_CONTAINERS = "{{ hostvars.nexus_linux.ansible_host_dns }}:8445"
        NEXUS_CREDENTIAL_ID = "nexus"
        DOCKER_IMAGE_NAME = "petclinic-docker"
        DOCKER_IMAGE = ""
    }
    stages {
        stage("Clone code from GitHub") {
            steps {
					git branch: 'main', url: '{{ github_project_url }}'
            }
        }
        stage("Maven Build") {
            steps {
                script {
                    sh "./mvnw clean package"
                }
            }
        }
        stage("Publish to Nexus Repository Manager") {
            steps {
                script {
                    pom = readMavenPom file: "pom.xml";
                    filesByGlob = findFiles(glob: "target/*.${pom.packaging}");
                    echo "${filesByGlob[0].name} ${filesByGlob[0].path} ${filesByGlob[0].directory} ${filesByGlob[0].length} ${filesByGlob[0].lastModified}"
                    artifactPath = filesByGlob[0].path;
                    artifactExists = fileExists artifactPath;
                    if(artifactExists) {
                        echo "*** File: ${artifactPath}, group: ${pom.groupId}, packaging: ${pom.packaging}, version ${pom.version}";
                        nexusArtifactUploader(
                            nexusVersion: NEXUS_VERSION,
                            protocol: NEXUS_PROTOCOL,
                            nexusUrl: NEXUS_URL,
                            groupId: pom.groupId,
                            version: VERSION,
                            repository: NEXUS_REPOSITORY,
                            credentialsId: NEXUS_CREDENTIAL_ID,
                            artifacts: [
                                [artifactId: pom.artifactId,
                                classifier: '',
                                file: artifactPath,
                                type: pom.packaging],
                                [artifactId: pom.artifactId,
                                classifier: '',
                                file: "pom.xml",
                                type: "pom"]
                            ]
                        );
                    } else {
                        error "*** File: ${artifactPath}, could not be found";
                    }
                }
            }
        }

        stage("Create DockerFile") {
            steps {
                script {
                    pom = readMavenPom file: "pom.xml";
                    def data = 'FROM openjdk:11\nWORKDIR /\nCOPY ./target/'+pom.artifactId+'-'+VERSION+'.jar'+' /target/\nEXPOSE 8080\nCMD java -jar /target/'+pom.artifactId+'-'+VERSION+'.jar'
                    writeFile(file: 'Dockerfile', text: data)
                }
            }
        }

        stage("Create Docker Image") {
            steps {
                script {
                    pom = readMavenPom file: "pom.xml";
                    DOCKER_IMAGE = docker.build("${DOCKER_IMAGE_NAME}-${VERSION}")
                }
            }
        }

        stage("Upload Docker Image to Nexus") {
            steps {
                script {
                    docker.withRegistry( 'https://'+NEXUS_URL_CONTAINERS, NEXUS_CREDENTIAL_ID ) {
                        DOCKER_IMAGE.push('latest')
                    }
                }
            }
        }
    }
}
